 function procesarRespuesta(ArrDataSource)  {
        let Ordenes = [];
        let Articulos = [];
        let Caracteristicas = [];
        let Composicion = [];
        ArrDataSource = (!Array.isArray(ArrDataSource)) ? ArrDataSource.OrdenesProduccion.ASWTraOrdenProduccion : null;

        if (!ArrDataSource) {
            return null
        }

        Ordenes = ArrDataSource.map(orden => { return orden.att });

        Ordenes = Ordenes.map(orden => { orden.IdPO = parseInt(orden.IdPO); return orden });
        Articulos = ArrDataSource.map(orden => {
            //CREA UN ARREGLO CON EL REGISTRO DE DE CatArticulo CORRESPONDIENTE
            let auxArr = []
            let idPO = orden.att.IdPO //EXTRAIGO EL ID DE LA ORDEN DE PRODUCCION 
            auxArr.push(idPO) //AGREGO EL ID DE LA OP POR QUE POSTERIOR MENTE SE FILTRARAN CON LAS ORDENES A INSERTAR
            auxArr.push(orden.AswCatArticuloGeneral[0].att)
            return auxArr;

        });

        //PROCESAR LAS CARACTERISTICAS DEL ARTICULO 
        let arrCaracteristicas = [] //ARREGLO AUXILIAR PARA EXTRAER LAS CARACTERISTICAS

        Caracteristicas = ArrDataSource.map(orden => { return orden.AswCatArticuloGeneral[0].AswCatArticuloCaracteristicas });
        Caracteristicas = Caracteristicas.map(caracteristicas => { return caracteristicas.map(x => x.att) })
        Caracteristicas.forEach(articulo => {
            articulo.forEach(feature => {
                feature["llave"] = `${feature["ClaArticulo"]}${feature["ClaCaracteristica"]}`;
                arrCaracteristicas.push(feature)
            });
        })

        Caracteristicas = arrCaracteristicas;

        Caracteristicas = msg.quitaRepetidos(Caracteristicas)

        //PROCESAR LA COMPOSICION DEL ARTICULO 
        let arrComposicion = [] //ARREGLO AUXILIAR PARA EXTRAER LAS CARACTERISTICAS

        Composicion = ArrDataSource.map(orden => { return orden.AswCatArticuloComposicion });
        Composicion = Composicion.map(composicion => { return composicion.map(x => x.att) });
        Composicion.forEach(articulo => {
            articulo.forEach(feature => {
                feature["llave"] = `${feature["ClaArticulo"]}${feature["ClaMateriaPrima"]}`;
                arrComposicion.push(feature)
            });
        })

        Composicion = arrComposicion;

        Composicion = msg.quitaRepetidos(Composicion)

        return { Ordenes, Articulos, Caracteristicas, Composicion }
}






