/**
 * DEVUELVE UN ARREGLO CON SOLO LOS ARTICULOS DE LAS ORDENES A INSERTAR
 * @param {Array} arrArticulos 
 * @param {Array} ordenesInsertar 
 */
function getArticulosInsertar(arrArticulos, ordenesInsertar) {

    let idOrdenesInsertar = ordenesInsertar.map(orden => orden.IdPO);
    arrArticulos = arrArticulos.filter(articulo => idOrdenesInsertar.indexOf(articulo[0]) != -1)
        .map(articulo => { articulo[1]["llave"] = articulo[1]["ClaArticulo"]; return articulo[1] });
    arrArticulos = msg.quitaRepetidos(arrArticulos);
    return arrArticulos;
}

/**
 * DEVUELVE UN ARREGLO CON LAS CARACTERISTICAS DE LOS ARTICULOS A INSERTAR
 * @param {array} arrCaracteristicas 
 * @param {array} articulosInsertar 
 */
function getCaracteristicasInsertar(arrCaracteristicas, articulosInsertar) {
    let claArtInsertar = articulosInsertar.map(articulo => articulo.ClaArticulo);
    arrCaracteristicas = arrCaracteristicas.filter(caracteristica => claArtInsertar.indexOf(caracteristica.ClaArticulo) != -1)
    return arrCaracteristicas
}

/**
 * DEVUELVE UN ARREGLO CON LA COMPOSICION DE LOS ARTICULOS A INSERTAR
 * @param {array} arrComposicion 
 * @param {array} articulosInsertar 
 */
function getComposicionInsertar(arrComposicion, articulosInsertar) {
    let claArtInsertar = articulosInsertar.map(articulo => articulo.ClaArticulo);
    arrComposicion = arrComposicion.filter(caracteristica => claArtInsertar.indexOf(caracteristica.ClaArticulo) != -1)
    return arrComposicion
}

